"use strict";

// Hàm tạo dữ liệu ngẫu nhiên cho biểu đồ
var randomChartData = function (r) {
    // Mảng để chứa dữ liệu
    var data = [];
    for (var i = 0; i < r; i++) {
        data.push(Math.round(200 * Math.random()));
    }
    return data;
};

// Màu sắc cho biểu đồ
var chartColors = {
    default: {
        primary: "#00D1B2",
        info: "#209CEE",
        danger: "#FF3860"
    }
};

// Lấy đối tượng canvas từ id "big-line-chart"
var ctx = document.getElementById("big-line-chart").getContext("2d");

// Tạo biểu đồ mới sử dụng thư viện Chart.js
new Chart(ctx, {
    type: "line",
    data: {
        // Các dữ liệu và cài đặt cho từng dataset trong biểu đồ
        datasets: [
            {
                fill: false,
                borderColor: chartColors.default.primary,
                borderWidth: 2,
                borderDash: [],
                borderDashOffset: 0,
                pointBackgroundColor: chartColors.default.primary,
                pointBorderColor: "rgba(255,255,255,0)",
                pointHoverBackgroundColor: chartColors.default.primary,
                pointBorderWidth: 20,
                pointHoverRadius: 4,
                pointHoverBorderWidth: 15,
                pointRadius: 4,
                data: randomChartData(9)
            },
            {
                fill: false,
                borderColor: chartColors.default.info,
                borderWidth: 2,
                borderDash: [],
                borderDashOffset: 0,
                pointBackgroundColor: chartColors.default.info,
                pointBorderColor: "rgba(255,255,255,0)",
                pointHoverBackgroundColor: chartColors.default.info,
                pointBorderWidth: 20,
                pointHoverRadius: 4,
                pointHoverBorderWidth: 15,
                pointRadius: 4,
                data: randomChartData(9)
            },
            {
                fill: false,
                borderColor: chartColors.default.danger,
                borderWidth: 2,
                borderDash: [],
                borderDashOffset: 0,
                pointBackgroundColor: chartColors.default.danger,
                pointBorderColor: "rgba(255,255,255,0)",
                pointHoverBackgroundColor: chartColors.default.danger,
                pointBorderWidth: 20,
                pointHoverRadius: 4,
                pointHoverBorderWidth: 15,
                pointRadius: 4,
                data: randomChartData(9)
            }
        ],
        // Nhãn cho trục x của biểu đồ
        labels: ["01", "02", "03", "04", "05", "06", "07", "08", "09"]
    },
    options: {
        maintainAspectRatio: false,
        legend: {
            display: false
        },
        responsive: true,
        tooltips: {
            backgroundColor: "#f5f5f5",
            titleFontColor: "#333",
            bodyFontColor: "#666",
            bodySpacing: 4,
            xPadding: 12,
            mode: "nearest",
            intersect: 0,
            position: "nearest"
        },
        scales: {
            yAxes: [
                {
                    barPercentage: 1.6,
                    gridLines: {
                        drawBorder: false,
                        color: "rgba(29,140,248,0.0)",
                        zeroLineColor: "transparent"
                    },
                    ticks: {
                        padding: 20,
                        fontColor: "#9a9a9a"
                    }
                }
            ],
            xAxes: [
                {
                    barPercentage: 1.6,
                    gridLines: {
                        drawBorder: false,
                        color: "rgba(225,78,202,0.1)",
                        zeroLineColor: "transparent"
                    },
                    ticks: {
                        padding: 20,
                        fontColor: "#9a9a9a"
                    }
                }
            ]
        }
    }
});
